import unittest


class Solution(object):
    def longestPalindrome(self, s):
        """
        :type s: str
        :rtype: str
        """
        if len(s) == 1:
            return s
        if len(s) == 0:
            return ""

        for i in range(len(s), 0, -1):
            for j in range(len(s) - i + 1):
                if s[j:i+j] == s[j:i+j][::-1]:
                    return s[j:i+j]


class Test(unittest.TestCase):
    def setUp(self):
        self.solution = Solution()

    def test_0(self):
        str1 = ""
        self.assertEqual(self.solution.longestPalindrome(str1), "hpyyph")



if __name__ == '__main__':
    unittest.main()
